<?phpnamespace DoctorScript\Hydrator;interface HydratorInterface{    /**     * Populate object properties named as $data array keys if any property exists in passed object     *     * @param  object $object     * @param  array $data     * @return void    */    public function hydrate($object, array $data);        /**     * By default export all object properties to array     * If $skipProps is used, export only not skipped properties     *     * @param  object $object     * @param  array $skipProps     * @return array object properties    */    public function export($object, array $skipProps = []);}