<?phpnamespace DoctorScript\Router;interface RouteMatchInterface{    /**     * Set matched route params     *     * @param  array $params     * @return void    */    public function setParams(array $params);        /**     * Get matched route params     *     * @return array    */    public function getParams();        /**     * Add param to matched route params     *     * @param  string $name     * @param  mixed $value     * @param  bool $allowOverride     * @return void    */    public function addParam($name, $value, $allowOverride);        /**     * Get param by name     *     * @param string $name     * @param mixed $default     * @return mixed    */    public function getParam($name, $default);        /**     * Check if has param     *     * @param  string $name     * @return bool true if has param or false otherwise    */    public function hasParam($name);}